fun main() {
    val startT = System.currentTimeMillis()
    fun minWindow(s: String, t: String): String {
        if (t.length == 1) return if (s.contains(t)) t else ""
        val tIndexMap = HashMap<Char, ArrayList<Int>>()
        val sIndexMap = HashMap<Char, ArrayList<Int>>()
        for (s1 in s.indices) {
            if (sIndexMap.containsKey(s[s1])) sIndexMap[s[s1]]?.add(s1)
            else sIndexMap[s[s1]] = arrayListOf(s1)
        }
        for (i in t.toList()) if (sIndexMap.containsKey(i)) tIndexMap[i] = sIndexMap[i]!!

        val s0 = "$s#"
        var minSize = s0.length
        var minStart: Int? = null
        var minEnd: Int? = null
        if (s == t) return t

        val singleList = ArrayList<Single>()
        fun onSingleMapEmpty(single: Single) {
            val list = single.tmp.sortedBy { it }
            val end = list[list.size - 1]
            val start = list[0]
            if (end == start) return
            if (end - start < minSize) {
                minSize = end - start
                minStart = start
                minEnd = end
            }
        }

        val needLength = s0.length - t.length
        for (i in s0.indices) {
//            if (singleList.size != 0) for (single in singleList.size - 1 downTo 0) {
//                val single0 = singleList[single]
//                val map = single0.map
//                if (map.size == 0) {
//                    onSingleMapEmpty(single0)
//                    singleList.removeAt(single)
//                }
//                if (map.size > minSize) continue
//                if (map.containsKey(s0[i])) {
//                    if (single0.tmp.size == tIndexMap[s0[i]]!!.size) {
//                        single0.tmp.remove(map[s0[i]]!![0])
//                        single0.tmp.add(i)
//                        map[s0[i]]!!.removeAt(0)
//                        if (map[s0[i]]!!.size == 0) {
//                            onSingleMapEmpty(single0)
//                            singleList.removeAt(single)
//                        }
//                        continue
//                    }
//                    single0.tmp.add(i)
//                    map[s0[i]]!!.removeAt(0)
//                }
//            }
//

            if (i > needLength) continue
            if (tIndexMap.containsKey(s0[i])) {
                val tmp = hashSetOf(i)
                val map = HashMap<Char, ArrayList<Int>>()
                map.putAll(tIndexMap)
                map[s0[i]]?.removeAt(0)
                singleList.add(Single(map, tmp))
            }
            if (singleList.size != 0) for (singleIndex in singleList.size - 1 downTo 0) {
                val single = singleList[singleIndex]
                val singleMap = single.map
                val singleTmp = single.tmp

                if (singleMap.size == 0) continue
//                if (singleMap.size > minSize) continue
                if (singleMap.containsKey(s0[i])) {
                    if (singleMap[s0[i]]!!.size != 0) {
                        if (singleTmp.size == tIndexMap[s0[i]]!!.size) {
                            singleTmp.remove(singleMap[s0[i]]!![0])
                        }
                        singleTmp.add(i)
                        singleMap[s0[i]]!!.removeAt(0)
                    } else {
                        onSingleMapEmpty(single)
                        singleList.removeAt(singleIndex)
                    }
                }
            }
        }

        if (minStart != null && minEnd != null) {
            var result = ""
            for (i in minStart!!..minEnd!!) {
                result += s0[i]
            }
            return result
        }
        return ""
    }

    val s =
        ""
    val t =
        ""

    println(minWindow(s, t))
    println(System.currentTimeMillis() - startT)
}

/**
 * @param map:字节对应的index
 * @param tmp:全体索引列表
 **/
class Single(var map: HashMap<Char, ArrayList<Int>>, var tmp: HashSet<Int> = HashSet())