package m001t099

fun main() {
    fun longestPalindrome(s: String): String {
        val start = System.currentTimeMillis()
        var maxSize = 0
        var result = ""
        val sSize = s.length - 1
        for (i in s.indices) {
            loop@ for (j in sSize downTo i) {
                if (s[i] == s[j]) {
                    var tmp = ""
                    var max: Int? = null
                    var min: Int? = null
                    val end = (j - i) shr 1
                    for (k in 0..end) {
                        if (s[i + k] != s[j - k]) continue@loop
                        if (max == null) max = j - k
                        if (min == null) min = i + k
                        if (maxSize > max - min) continue@loop else if (k == end) {
                            maxSize = max - min
                        }
                    }
                    for (k in min!!..max!!) tmp += s[k]
                    if (tmp.length >= result.length) result = tmp
                }
            }
        }
        println(System.currentTimeMillis() - start)
        return result
    }

    // 0ms
    val s0 =
        ""
    // 25ms
    val s1 =
        ""
    // 11ms
    val s2 =
        ""

    println(longestPalindrome(s1))

}